// Parse AI response and combine with original data
const inputData = items[0].json;
const aiResponse = inputData.output || inputData.response || inputData.content;

try {
  // Clean and parse AI response
  let cleanedResponse = aiResponse.trim();

  // Remove any markdown code blocks
  cleanedResponse = cleanedResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '');

  // Find JSON object in response
  const jsonStart = cleanedResponse.indexOf('{');
  const jsonEnd = cleanedResponse.lastIndexOf('}') + 1;

  if (jsonStart >= 0 && jsonEnd > jsonStart) {
    cleanedResponse = cleanedResponse.substring(jsonStart, jsonEnd);
  }

  // Parse JSON
  const aiContent = JSON.parse(cleanedResponse);

  // Combine original data with AI generated content
  const result = {
    // Original input data
    processing_id: inputData.processing_id,
    guest_name: inputData.guest_name,
    guest_email: inputData.guest_email,
    profile_text: inputData.profile_text,
    job_description: inputData.job_description,
    prompt_type: inputData.prompt_type,
    oracle_style: inputData.oracle_style,
    timestamp: inputData.timestamp,
    oracle_loaded: inputData.oracle_loaded,

    // AI generated content
    ...aiContent,

    // Metadata
    ai_model: "llama3.2:1b",
    generation_timestamp: new Date().toISOString(),
    content_quality: "professional",
    automation_level: "100%"
  };

  return [{ json: result }];

} catch (error) {
  // Error handling
  return [{
    json: {
      error: true,
      error_message: "Failed to parse AI response: " + error.message,
      raw_ai_response: aiResponse,
      processing_id: inputData.processing_id,
      guest_name: inputData.guest_name,
      oracle_style: inputData.oracle_style,
      timestamp: new Date().toISOString()
    }
  }];
}
