// Load and process oracle prompt
const inputData = items[0].json;
const binaryData = items[0].binary;

try {
  // Read the oracle prompt file
  const oracleContent = Buffer.from(binaryData.data.data, 'base64').toString('utf8');
  const oraclePrompt = JSON.parse(oracleContent);

  // Build the prompt based on oracle template
  let finalPrompt;

  if (inputData.prompt_type === 'corporate_climber') {
    finalPrompt = `Create a professional podcast interview prep package for:

Name: ${inputData.guest_name}
Email: ${inputData.guest_email}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Generate a CORPORATE CLIMBER style interview package focusing on:
- Executive leadership development
- Strategic decision-making and business impact
- Team management and organizational growth
- Boardroom presence and corporate excellence

Return ONLY valid JSON in this exact format:
{
  "episode_title": "Executive leadership focused title",
  "guest_bio": "Professional biography emphasizing leadership credentials",
  "leadership_story_1": "Key leadership achievement with business impact",
  "leadership_story_2": "Strategic decision that drove results",
  "team_impact_story": "Team development or organizational transformation",
  "question_1": "Strategic leadership and vision question",
  "question_2": "Team management and people development question",
  "question_3": "Business strategy and market positioning question",
  "professional_bio": "Extended executive summary with quantified achievements",
  "talking_points": "Key discussion points for executive-level interview"
}

Focus on leadership impact, strategic thinking, business results, and executive presence.`;
  } else {
    // Default prompt
    finalPrompt = `Create a professional podcast episode package for:

Name: ${inputData.guest_name}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Return valid JSON only:
{
"episode_title": "title",
"guest_bio": "bio",
"industry_focus": "industry",
"win_story_1": "story 1",
"win_story_2": "story 2",
"question_1": "question?",
"question_2": "question?", 
"question_3": "question?",
"key_insights": ["insight1", "insight2", "insight3"],
"episode_summary": "summary"
}`;
  }

  // Return combined data
  return [{
    json: {
      ...inputData,
      oracle_prompt: oraclePrompt,
      final_prompt: finalPrompt,
      oracle_loaded: true
    }
  }];

} catch (error) {
  // Fallback if oracle file not found
  const fallbackPrompt = `Create a professional podcast episode package for:

Name: ${inputData.guest_name}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Return valid JSON only:
{
"episode_title": "title",
"guest_bio": "bio",
"industry_focus": "industry",
"win_story_1": "story 1",
"win_story_2": "story 2",
"question_1": "question?",
"question_2": "question?", 
"question_3": "question?",
"key_insights": ["insight1", "insight2", "insight3"],
"episode_summary": "summary"
}`;

  return [{
    json: {
      ...inputData,
      final_prompt: fallbackPrompt,
      oracle_loaded: false,
      oracle_error: error.message
    }
  }];
}

// 🎯 CHUCK ACADEMY - ULTIMATE ERROR-AS-FEATURE PROCESSOR 💪
const inputData = items[0].json;
const aiResponse = inputData.output || inputData.response || inputData.content || inputData.text;

// Debug: log what we received
console.log('🔍 AI Response received:', aiResponse);
console.log('📊 Input data keys:', Object.keys(inputData));

// 🚨 CHUCK ACADEMY TRIGGERS:
// 1. No AI response
// 2. AI parsing fails
// 3. Invalid JSON structure
// 4. AI returns garbage (like Slack docs)

function generateChuckAcademy(userData, errorDetails) {
  // 🎲 Chuck Norris Academy Content Generator
  const chuckJokes = [
    "Chuck Norris doesn't need a resume. Jobs apply to him.",
    "When Chuck Norris updates his LinkedIn, the servers thank him.",
    "Chuck Norris once got a job by staring at the application form.",
    "HR departments exist because Chuck Norris allows them to.",
    "Chuck Norris doesn't interview for jobs. Jobs interview for Chuck Norris.",
    "When Chuck Norris enters a career fair, all the companies line up for him.",
    "Chuck Norris's career advice: Be Chuck Norris. Everything else is optional.",
    "The only networking Chuck Norris needs is a roundhouse kick.",
    "Chuck Norris doesn't have a career. Careers have Chuck Norris.",
    "When Chuck Norris submits a resume, it's automatically accepted by the universe."
  ];

  const careerAdvice = [
    "🎯 Pro Tip: Even when AI fails, YOU don't have to. Keep pushing forward!",
    "💡 Career Wisdom: The best professionals adapt when technology doesn't work.",
    "🚀 Success Secret: Turn every setback into a comeback story.",
    "⚡ Power Move: Use failures as learning opportunities to stand out.",
    "🎪 Reality Check: If our AI can't handle your awesomeness, imagine what employers will think!"
  ];

  const careerLinks = [
    "🔗 LinkedIn Career Advice: https://linkedin.com/advice/career",
    "📚 Harvard Business Review: https://hbr.org/topic/career-planning",
    "💼 Indeed Career Guide: https://indeed.com/career-advice",
    "🎯 Glassdoor Career Insights: https://glassdoor.com/blog/career-advice",
    "🚀 Forbes Career Tips: https://forbes.com/careers",
    "💡 Monster Career Advice: https://monster.com/career-advice",
    "⭐ The Muse Career Guide: https://themuse.com/advice",
    "🎪 CareerBuilder Resources: https://careerbuilder.com/advice",
    "🔥 Fast Company Career Tips: https://fastcompany.com/section/career",
    "💪 Entrepreneur Career Growth: https://entrepreneur.com/topic/career"
  ];

  // Random selections for uniqueness
  const randomJoke = chuckJokes[Math.floor(Math.random() * chuckJokes.length)];
  const randomAdvice = careerAdvice[Math.floor(Math.random() * careerAdvice.length)];
  const randomLinks = careerLinks.sort(() => 0.5 - Math.random()).slice(0, 3);

  // Generate industry-specific career suggestions
  const profile = userData.profile_text || '';
  const jobDesc = userData.job_description || '';
  let careerSuggestions = [];

  if (profile.toLowerCase().includes('banking') || profile.toLowerCase().includes('finance')) {
    careerSuggestions = ['Financial Analyst', 'Investment Banker', 'Risk Manager', 'Portfolio Manager', 'Financial Advisor'];
  } else if (profile.toLowerCase().includes('tech') || profile.toLowerCase().includes('software')) {
    careerSuggestions = ['Software Engineer', 'Product Manager', 'DevOps Engineer', 'Data Scientist', 'UX Designer'];
  } else if (profile.toLowerCase().includes('marketing') || profile.toLowerCase().includes('sales')) {
    careerSuggestions = ['Digital Marketing Manager', 'Sales Director', 'Brand Manager', 'Content Strategist', 'Growth Hacker'];
  } else {
    careerSuggestions = ['Project Manager', 'Business Analyst', 'Operations Manager', 'Consultant', 'Team Lead'];
  }

  return {
    // Original user data (always preserved)
    processing_id: userData.processing_id,
    guest_name: userData.guest_name,
    guest_email: userData.guest_email,
    profile_text: userData.profile_text,
    job_description: userData.job_description,
    prompt_type: userData.prompt_type,
    timestamp: userData.timestamp,

    // Chuck Academy content
    episode_title: `🎪 Chuck Norris Academy: ${userData.guest_name}'s Career Adventure`,
    guest_bio: `Meet ${userData.guest_name}, a professional who's about to get some legendary career advice from Chuck Norris himself!`,
    chuck_joke: randomJoke,
    chuck_advice: randomAdvice,
    career_suggestions: careerSuggestions,
    career_links: randomLinks,

    // Error context (educational)
    error_context: errorDetails,
    ai_training_tip: "🤖 AI Training Tip: Be specific with your experience and clear about your goals. The more details you provide, the better results you'll get!",

    // Professional encouragement
    encouragement: `Hey ${userData.guest_name}! Even when AI gets confused, your career potential is crystal clear. Let's turn this tech hiccup into your success story!`,

    // Metadata
    content_type: "chuck_academy",
    ai_model: "chuck_norris_wisdom_v1.0",
    generation_timestamp: new Date().toISOString(),
    content_quality: "legendary",
    automation_level: "100% Chuck Approved",
    oracle_loaded: userData.oracle_loaded || false,

    // Academy specific
    academy_session: Math.floor(Math.random() * 1000) + 1,
    chuck_rating: "⭐⭐⭐⭐⭐",
    user_level: "Career Warrior in Training"
  };
}

// 🎯 MAIN PROCESSING LOGIC
if (!aiResponse || aiResponse.trim() === '') {
  // No AI response - Chuck Academy time!
  console.log('🎪 Triggering Chuck Academy: No AI response');
  return [{
    json: generateChuckAcademy(inputData, "AI didn't respond - maybe it was intimidated by your awesomeness!")
  }];
}

try {
  // Try to parse AI response
  let cleanedResponse = aiResponse ? aiResponse.trim() : '';

  // Remove markdown code blocks
  cleanedResponse = cleanedResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '');

  // Check for obvious garbage responses (like Slack documentation)
  if (cleanedResponse.toLowerCase().includes('slack') || 
      cleanedResponse.toLowerCase().includes('webhook') ||
      cleanedResponse.toLowerCase().includes('token') ||
      cleanedResponse.length > 2000) {
    console.log('🎪 Triggering Chuck Academy: AI returned garbage');
    return [{
      json: generateChuckAcademy(inputData, "AI got confused and started talking about Slack - time for Chuck wisdom!")
    }];
  }

  // Find JSON object in response
  const jsonStart = cleanedResponse.indexOf('{');
  const jsonEnd = cleanedResponse.lastIndexOf('}') + 1;

  if (jsonStart >= 0 && jsonEnd > jsonStart) {
    cleanedResponse = cleanedResponse.substring(jsonStart, jsonEnd);
  } else {
    console.log('🎪 Triggering Chuck Academy: No JSON found');
    return [{
      json: generateChuckAcademy(inputData, "AI response didn't contain valid JSON - Chuck to the rescue!")
    }];
  }

  // Parse JSON
  const aiContent = JSON.parse(cleanedResponse);

  // Validate AI content has required fields
  if (!aiContent.episode_title || !aiContent.guest_bio) {
    console.log('🎪 Triggering Chuck Academy: Invalid AI content structure');
    return [{
      json: generateChuckAcademy(inputData, "AI response was missing key fields - Chuck Academy activated!")
    }];
  }

  // 🎉 SUCCESS! AI worked properly
  console.log('✅ AI Success: Professional content generated');
  const result = {
    // Original input data
    processing_id: inputData.processing_id,
    guest_name: inputData.guest_name,
    guest_email: inputData.guest_email,
    profile_text: inputData.profile_text,
    job_description: inputData.job_description,
    prompt_type: inputData.prompt_type,
    timestamp: inputData.timestamp,
    oracle_loaded: inputData.oracle_loaded,

    // AI generated content
    ...aiContent,

    // Metadata
    content_type: "professional",
    ai_model: "llama3.2:1b",
    generation_timestamp: new Date().toISOString(),
    content_quality: "professional",
    automation_level: "100%"
  };

  return [{ json: result }];

} catch (error) {
  // 🎪 ANY PARSING ERROR = CHUCK ACADEMY!
  console.log('🎪 Triggering Chuck Academy: JSON parsing failed -', error.message);
  return [{
    json: generateChuckAcademy(inputData, `JSON parsing failed: ${error.message} - But Chuck never fails!`)
  }];
}

// 🎪 SIMPLE CHUCK ACADEMY HTML GENERATOR (PROVEN TO WORK!)
const inputData = items[0].json;
const contentType = inputData.content_type || 'professional';

console.log('🎨 HTML Generator - Content Type:', contentType);

if (contentType === 'chuck_academy') {
  // 🎪 CHUCK ACADEMY MODE - Using the proven simple template!
  console.log('🎪 Generating Chuck Academy HTML');

  // Simple template that we know works!
  const htmlContent = `<!DOCTYPE html>
<html>
<head>
    <title>🎪 Chuck Norris Academy</title>
    <style>
        body { font-family: Arial; background: #1e3c72; color: white; padding: 20px; }
        .container { max-width: 800px; margin: 0 auto; background: rgba(255,255,255,0.1); padding: 30px; border-radius: 15px; }
