            <p><strong>Profile:</strong> ${inputData.profile_text}</p>
        </div>

        <div class="chuck-quote">
            "${inputData.chuck_joke}"
        </div>

        <div class="section">
            <h3>💡 Chuck's Career Advice</h3>
            <p>${inputData.chuck_advice}</p>
        </div>

        <div class="section">
            <h3>🚀 Recommended Career Paths</h3>
            ${inputData.career_suggestions.map(career => `<div class="career-item">${career}</div>`).join('')}
        </div>

        <div class="section">
