            ${inputData.career_links.map(link => `<p><a href="${link}" style="color: #48dbfb;">${link}</a></p>`).join('')}
        </div>

        <div class="section">
            <h3>🤖 What Happened</h3>
            <p><strong>Tech Issue:</strong> ${inputData.error_context}</p>
            <p><strong>Encouragement:</strong> ${inputData.encouragement}</p>
        </div>

        <div style="text-align: center; margin-top: 30px; font-size: 0.9em; opacity: 0.8;">
            <p>Session: ${inputData.academy_session} | Rating: ${inputData.chuck_rating} | Quality: ${inputData.content_quality}</p>
            <p>Generated: ${inputData.generation_timestamp}</p>
        </div>
    </div>
</body>
</html>`;

  return [{
    json: {
      ...inputData,
      html_content: htmlContent,
      html_type: "chuck_academy",
      file_name: `chuck-academy-${inputData.processing_id}.html`,
      title: inputData.episode_title,
      generation_status: "chuck_academy_success"
    }
  }];

} else {
  // ✅ PROFESSIONAL MODE - AI worked perfectly!
  console.log('✅ Generating Professional Interview HTML');

  const professionalHtml = `<!DOCTYPE html>
<html>
<head>
    <title>${inputData.episode_title || 'Professional Interview'}</title>
    <style>
        body { font-family: Georgia, serif; background: #f8f9fa; color: #333; padding: 20px; }
        .container { max-width: 800px; margin: 0 auto; background: white; padding: 40px; border-radius: 10px; box-shadow: 0 5px 15px rgba(0,0,0,0.1); }
