    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <div class="title">${inputData.episode_title || 'Professional Interview Package'}</div>
        </div>

        <div class="section">
            <h3>Guest Information</h3>
            <p><strong>Name:</strong> ${inputData.guest_name}</p>
            <p><strong>Email:</strong> ${inputData.guest_email}</p>
            <p><strong>Profile:</strong> ${inputData.profile_text}</p>
            <p><strong>Role:</strong> ${inputData.job_description}</p>
        </div>

        <div class="section">
            <h3>Biography</h3>
            <p>${inputData.guest_bio || 'Professional biography will be generated here.'}</p>
        </div>

        <div style="margin-top: 40px; font-size: 0.9em; color: #6c757d;">
            <p>AI Model: ${inputData.ai_model} | Generated: ${inputData.generation_timestamp}</p>
            <p>Processing ID: ${inputData.processing_id}</p>
        </div>
    </div>
</body>
</html>`;

  return [{
    json: {
      ...inputData,
      html_content: professionalHtml,
      html_type: "professional",
      file_name: `interview-${inputData.processing_id}.html`,
      title: inputData.episode_title,
      generation_status: "professional_success"
    }
  }];
}
