// ğŸ”ï¸ Swiss Precision Data Validation & Processing
// Extract and validate incoming data

const inputData = $json.body || $json;

// Validate required fields
const requiredFields = ['name', 'email', 'linkedin_text'];
const missingFields = requiredFields.filter(field => !inputData[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Clean and structure data
const cleanedData = {
  name: inputData.name.trim(),
  email: inputData.email.trim().toLowerCase(),
  linkedin_text: inputData.linkedin_text.trim(),
  job_description: inputData.job_description?.trim() || '',
  company: inputData.company?.trim() || '',
  timestamp: new Date().toISOString(),
  processing_id: `podcast_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// Extract industry/field from LinkedIn text (basic analysis)
const industryKeywords = {
  'banking': ['bank', 'financial', 'credit', 'loan', 'investment'],
  'technology': ['software', 'tech', 'developer', 'programming', 'IT'],
  'healthcare': ['medical', 'health', 'hospital', 'patient', 'clinical'],
  'education': ['teacher', 'professor', 'education', 'student', 'university'],
  'marketing': ['marketing', 'brand', 'advertising', 'campaign', 'social media']
};

let detectedIndustry = 'professional';
for (const [industry, keywords] of Object.entries(industryKeywords)) {
  if (keywords.some(keyword => cleanedData.linkedin_text.toLowerCase().includes(keyword))) {
    detectedIndustry = industry;
    break;
  }
}

cleanedData.detected_industry = detectedIndustry;

console.log('âœ… Data validation complete:', cleanedData.processing_id);

return cleanedData;

// ğŸ­ AI Response Parser & Content Structurer
// Parse Ollama response and structure for template

const ollamaResponse = $json.response || $json.body;
let aiContent;

try {
  // Extract JSON from Ollama response
  const responseText = ollamaResponse.response || ollamaResponse;

  // Clean the response to extract just the JSON
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (!jsonMatch) {
    throw new Error('No JSON found in AI response');
  }

  aiContent = JSON.parse(jsonMatch[0]);

} catch (error) {
  console.error('âŒ Error parsing AI response:', error);
  // Fallback content structure
  aiContent = {
    episode_title: `Professional Insights with ${$('data-processor').item.json.name}`,
    guest_bio: `${$('data-processor').item.json.name} is a seasoned professional in the ${$('data-processor').item.json.detected_industry} industry.`,
    win_story_1: "Career Achievement Story",
    win_story_2: "Professional Milestone",
    question_1: "What inspired you to enter your field?",
    question_2: "What has been your biggest career achievement?",
    question_3: "What trends do you see in your industry?",
    question_4: "What advice would you give to newcomers?",
    question_5: "Where do you see the industry heading?",
    professional_bio: `Detailed professional biography for ${$('data-processor').item.json.name}.`,
    industry_focus: $('data-processor').item.json.detected_industry,
    value_proposition: "Bringing unique insights and expertise",
    call_to_action: "Connect on LinkedIn for more insights"
  };
}

// Merge with original data
const finalData = {
  ...$('data-processor').item.json,
  ...aiContent,
  generation_timestamp: new Date().toISOString(),
  ai_model: 'llama3.2:latest',
  content_quality: 'professional'
};

console.log('âœ… AI content processed:', finalData.processing_id);

return finalData;

