// ğŸš¨ Error Handler & Recovery
// Log error and provide meaningful response

const error = $json.error || $input.item.json.error || 'Unknown error occurred';
const errorDetails = {
  timestamp: new Date().toISOString(),
  error_message: error.toString(),
  processing_stage: 'unknown',
  recovery_action: 'manual_review_required'
};

// Determine which stage failed
if (error.toString().includes('webhook')) {
  errorDetails.processing_stage = 'webhook_input';
  errorDetails.recovery_action = 'check_webhook_payload';
} else if (error.toString().includes('ollama') || error.toString().includes('AI')) {
  errorDetails.processing_stage = 'ai_generation';
  errorDetails.recovery_action = 'retry_ai_generation';
} else if (error.toString().includes('email')) {
  errorDetails.processing_stage = 'email_delivery';
  errorDetails.recovery_action = 'check_smtp_config';
} else if (error.toString().includes('html') || error.toString().includes('template')) {
  errorDetails.processing_stage = 'html_generation';
  errorDetails.recovery_action = 'validate_template_data';
}

console.error('âŒ Podcast Studio Error:', errorDetails);

return {
  status: 'error',
  error_details: errorDetails,
  message: 'ğŸš¨ Podcast package generation failed',
  support_info: {
    contact: 'Check logs for details',
    retry: 'Possible - check error stage',
    fallback: 'PowerShell backup system available'
  }
};

